export type UserAuth = SharedSessionAuth | JwtAuth;
export type UserAuthType = UserAuth['type'];
export type SharedSessionAuth = {
    type: 'shared-session';
    apiUrl: string;
    loginUrl: string;
    invalidatedAt?: number;
};
export type JwtAuthSigningKey = {
    id: string;
    publicKey: string;
    createdAt: number;
};
export type JwtAuth = {
    type: 'jwt';
    signingKeys: JwtAuthSigningKey[];
    loginUrl: string;
    invalidatedAt?: number;
};
